import java.util.Scanner;
public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		java.util.Scanner scan = new Scanner(System.in);
		System.out.print("Введите число от которого будет счет");
		int a =scan.nextInt();
		System.out.print("Введите число до которого будет чет");
		int b = scan.nextInt();
	do {a++;
		System.out.println(a + " ");}
		while (a < b);// для продолжнения цикла должно быть истинное условие
		

	for (int i = 3; i>=-3; i--) { System.out.println(i);}
for (int d = 1; d<=5; d++)  { System.out.print(d);}
	for (int z=5; z>=1; z--) {System.out.print(z);
			
//int i = 100;
//while (i >= 10) { System.out.println(i + " ");

//i=i-10;
//}
	}

}}
